con:config.script:
//Get the value of "Source test case property" 
def tCValue = context.expand( '${#TestCase#TC_Source}' )
log.info "Test case property value is " + tCValue

//Get the value of "Source test suite property" 
def TSValue = context.expand( '${#TestSuite#Source test suite property}' )
log.info "Test suite property value is " + TSValue

//Get the value of "Source project property" 
def Pvalue = context.expand( '${#Project#Source project property}' )
log.info "Project property value is " + Pvalue


// Set the value of "Target test case property" 
testRunner.testCase.setPropertyValue( "TC_Dest", tCValue)

// Set the value of "Target test suite property" 
testRunner.testCase.testSuite.setPropertyValue( "Target test suite property", TSValue)

// Set the value of "Target project property" 
testRunner.testCase.testSuite.project.setPropertyValue( "Target project property", Pvalue )
